import React, { useState } from 'react';
import { useStore } from './MyStore';
import './index.css';

function MyComponent() {
    const store = useStore();

    // local state
    const [localData, setLocalData] = useState<any>({ mood: '', goodness: '', actofkindness: ''});

    // folosim datele din MobX store
    const myData = store.myData;

    // refresh la date
    // adaugam un obiect nou  la MobX store
    const handleAddObject = (event: React.FormEvent<HTMLFormElement>) => {

        event.preventDefault();
        if (!localData.mood || !localData.goodness || !localData.actofkindness) {
            alert('Introduceti toate campurile.');
            return;
        }
        store.addObject(localData);
        setLocalData({ mood: '', goodness: '', actofkindness: '',});
    };


// stergem obiectul din MobX store si  facem update la  local state
    const handleDeleteObject = (index: number) => {
        store.deleteObject(index);
        setLocalData({ mood: "", goodness: "", actofkindness: ""});
    };


    return (

        <div className="container">
            <h1 className="title">Be you and smile!</h1>
            <form className="form" onSubmit={handleAddObject}>
                <input className="input" type="text" placeholder="Range your mood from 1 to 10 " value={localData.mood} onChange={e => setLocalData({ ...localData, mood: e.target.value })} />
                <input className="input" type="text" placeholder="Range your goodness from 1 to 10 " value={localData.goodness} onChange={e => setLocalData({ ...localData, goodness: e.target.value })} />
                <input className="input" type="text" placeholder="Now, write your last act of kindness  " value={localData.actofkindness} onChange={e => setLocalData({ ...localData, actofkindness: e.target.value })} />
                <button className="button" type="submit">Trimite</button>
            </form>
            <div>
                <div className="grid">
                    {myData.map((data: any, index: number) => (
                        <div key={index} className="object">
                            <p>Mood: {data.mood}</p>
                            <p>Goodness: {data.goodness}</p>
                            <p>Act of kindness: {data.actofkindness}</p>

                            <button onClick={() => handleDeleteObject(index)}>Delete</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default MyComponent;
