import React, { useState, useEffect } from "react";

const UserInitial = [
{ username: "sanda", password: "hello", birthday: "01/01/1990", city: "New York", university: "NYU" },
{ username: "arina", password: "last", birthday: "02/02/1995", city: "Los Angeles", university: "UCLA" }
];

const App = () => {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const [isLoggedIn, setIsLoggedIn] = useState(false);
const [users, setUsers] = useState(UserInitial);

useEffect(() => {
const storedUsers = localStorage.getItem("users");
if (storedUsers) {
setUsers([...users, ...JSON.parse(storedUsers)]);
}
}, [users]);

const handleLogin = (event) => {
event.preventDefault();
const foundUser = users.find(user => user.username === username && user.password === password);
if (foundUser) {
setIsLoggedIn(true);
localStorage.setItem("isLoggedIn", true);
localStorage.setItem("user", JSON.stringify(foundUser));
alert("Autentificare reușită");
} else {
alert("Login eșuat");
}
};

const handleLogout = () => {
setIsLoggedIn(false);
localStorage.setItem("isLoggedIn", false);
alert("Succes la deconectare");
};

const renderLoginForm = () => {
return (
<form onSubmit={handleLogin}>
<label>
Username:
<input type="text" value={username} onChange={(event) => setUsername(event.target.value)} />
</label>
<br />
<label>
Password:
<input type="password" value={password} onChange={(event) => setPassword(event.target.value)} />
</label>
<br />
<button type="submit">Autentificare</button>
</form>
);
};

const renderLogoutButton = () => {
const storedUser = localStorage.getItem("user");
const loggedInUser = storedUser ? JSON.parse(storedUser) : null;
return (
<div>
{loggedInUser && (
<div>
<p>Username: {loggedInUser.username}</p>
<p>Birthday: {loggedInUser.birthday}</p>
<p>City: {loggedInUser.city}</p>
<p>University: {loggedInUser.university}</p>
</div>
)}
<button onClick={handleLogout}>Ieșire</button>
</div>
);
};

return <div>{isLoggedIn ? renderLogoutButton() : renderLoginForm()}</div>;
};

export default App;
