import React, { useState, useEffect } from "react";

const UserInitial = [
{ username: "sanda", password: "hello", birthday: "01/01/1990", university: "MIT", city: "Boston" },
{ username: "arina", password: "last", birthday: "02/02/1995", university: "Harvard", city: "Cambridge" },
];

const App = () => {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const [loggedInUser, setLoggedInUser] = useState(null);
const [users, setUsers] = useState(UserInitial);

useEffect(() => {
const storedUsers = localStorage.getItem("users");
if (storedUsers) {
setUsers([...users, ...JSON.parse(storedUsers)]);
}
}, [users]);

const handleLogin = (event) => {
event.preventDefault();
const foundUser = users.find(
(user) => user.username === username && user.password === password
);
if (foundUser) {
setLoggedInUser(foundUser);
localStorage.setItem("loggedInUser", JSON.stringify(foundUser));
alert("Autentificare reușită");
} else {
alert("Login eșuat");
}
setPassword("");
};

const handleLogout = () => {
setLoggedInUser(null);
localStorage.removeItem("loggedInUser");
alert("Succes la deconectare");
};

const renderLoginForm = () => {
return (
<form onSubmit={handleLogin}>
<label>
Username:
<input
type="text"
value={username}
onChange={(event) => setUsername(event.target.value)}
/>
</label>
<br />
<label>
Password:
<input
type="text"
value={password}
onChange={(event) => setPassword(event.target.value)}
/>
</label>
<br />
<button type="submit">Autentificare</button>
</form>
);
};

const renderLoggedInUserInterface = () => {
return (
<div>
<p>User: {loggedInUser.username}</p>
<p>Birthday: {loggedInUser.birthday}</p>
<p>University: {loggedInUser.university}</p>
<p>City: {loggedInUser.city}</p>
<button onClick={handleLogout}>Ieșire</button>
</div>
);
};

useEffect(() => {
const storedLoggedInUser = localStorage.getItem("loggedInUser");
if (storedLoggedInUser) {
setLoggedInUser(JSON.parse(storedLoggedInUser));
}
}, []);

return <div>{loggedInUser ? renderLoggedInUserInterface() : renderLoginForm()}</div>;
};

export default App;
